def longestPalindrome(s: str) -> str:
    if s == "":
        return s        
    left = 0
    right = len(s)
    while (right - left) > 0:
        right_aux = right
        while right_aux <= len(s):
            if s[left:right_aux] == s[left:right_aux][::-1]:
                return s[left:right_aux]   
            right_aux += 1
            left += 1
        left = 0
        right -= 1                
    return s[0]

a = ""
b = longestPalindrome(a)
print(a,b)